mapper.select:
SELECT tmp.*, provider.providername AS providername FROM bus_provider AS provider JOIN ( SELECT inport.id, inport.providerid, inport.operateperson, inport.paytype, sum( inport.price ) AS priceSum FROM bus_inport AS inport JOIN bus_goods AS goods ON 1 = 1 AND goods.id = inport.goodsid
and inport.id like #{id}
GROUP BY inport.id ) AS tmp on 1=1 and provider.id = tmp.providerid
and provider.id =#{providerid}
order by tmp.id desc LIMIT #{page},#{limit}

